package Review;

public class ForloopRe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=0; i<5; i++) {
			System.out.println(i);
		}
		
		int i=0;
		
		for(System.out.println("x"); i<5; System.out.println("y")) {
			i++;
			System.out.println(i);
		}
		
		for(int a=0; i<5; i++) {
			i++;
			System.out.println(a);
			
		}
	}

}
