package com.syntax.class06and07;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;
		
		while(a<=3) {
			System.out.println("Hi");
			a++;
			
		}
		
		int b=1;
		
		do {
			
			System.out.println("Hello");
			
		} while(b>=3);

    }
}
