package com.syntax.class10;

public class AllElements2dArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String[][] gro = { { "apple", "banana", "kiwi" }, { "potato", "tomato" },{ "milk", "cheese", "yogurt", "ayran" } };
	
	
		for(String[] groc:gro) {
			for(String g:groc) {
				System.out.print(g+" ");
			}
			
			
			System.out.println();
		
		}
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	}

}
