package com.syntax.class2and3;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// (==) if equal (> greater=true vuseversa with<) (To find if true or false)
		int number1=20;
		int number2=20;
		// boolean results=number1==number2; (another way)
		System.out.println(number1==number2); // checks if both variables contain same thing
		//System.out.println(results);
		
		System.out.println(number1>number2); // greater than
		System.out.println(number1<number2); // less than
		
		System.out.println(number1<=number2); // greater or =
		System.out.println(number1>=number2); //less than or =
		
		System.out.println(number1!=number2); // not equal to
	





		
		



	}

}
