package com.syntax.class2and3;

public class ShortHandOptDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=10;
		number=number+1;
		System.out.println(number);

		number++; // Shorter way of doing number=number+1 
		System.out.println(number);
		
		number=10;
		number=number+30;
		System.out.println(number);
		number+=30; // same as number=number+30;
		System.out.println(number);
		
		number=10;
		number-=5;
		System.out.println(number);
		
		number=10;
		number*=5;
		System.out.println(number);
		
		number=10;
		number/=5;
		System.out.println(number);
		
		number=10;
		number%=5;
		System.out.println(number);

				
				

			


				

				

				
		
	}

}
