		System.out.println(number);
		
		// Declare variable and decrease by 67 using shorthand opt
		int number2=100;
		number2-=67;
		System.out.println(number2);
		
		// Declare variable cakePiece=11 and divide cakepiece between 4 people using shorthand opt 
		int cakePiece=11;
		// cakepiece/=4
		System.out.println(cakePiece/4);
		System.out.println(cakePiece%4);
		
	// Declare variable cakepiece=25 and divide cakepiece between 7 people, Using shorthand opt find out how many pieces cake left after was distrubte qually to 7 people
		cakePiece=25;
		cakePiece/=7; //cakepiece=cakepiece/7
